<?php

namespace Euro\CoinBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * CoinRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CoinRepository extends EntityRepository {

	public function findByCountry(array $countries) {
		$queryBuidler = $this->createQueryBuilder('c');
		$expr = $queryBuidler->expr();

		return $queryBuidler
						->select('c, ct, v, y, w')
						->join('c.country', 'ct')
						->join('c.value', 'v')
						->join('c.year', 'y')
						->leftJoin('y.workshop', 'w')
						->where($expr->in('c.country', ':countries'))
						->orderBy('y.year', 'ASC')
						->addOrderBy('w.short_name', 'ASC')
						->addOrderBy('v.value', 'DESC')
						->setParameter('countries', $countries)
						->getQuery()
						->getResult();
	}

	public function findCoinById(array $ids) {
		$queryBuidler = $this->createQueryBuilder('c');
		$expr = $queryBuidler->expr();

		return $queryBuidler
						->select('c, ct, v, y, w')
						->join('c.country', 'ct')
						->join('c.value', 'v')
						->join('c.year', 'y')
						->leftJoin('y.workshop', 'w')
						->where($expr->in('c.id', ':coins'))
						->orderBy('y.year', 'ASC')
						->addOrderBy('w.short_name', 'ASC')
						->addOrderBy('v.value', 'DESC')
						->setParameter('coins', $ids)
						->getQuery()
						->getResult();
	}

	public function findCoinsByCountry(Country $country, $collector, $year) {
		$queryBuidler = $this->createQueryBuilder('c');
		$expr = $queryBuidler->expr();
		$parameters = array(
			'collector' => $collector,
			'country' => $country,
		);

		$queryBuilder = $queryBuidler
				->select('c, v, y, w')
				->join('c.value', 'v')
				->join('c.year', 'y')
				->leftJoin('y.workshop', 'w')
				->where($expr->eq('c.country', ':country'))
				->andWhere($expr->eq('c.collector', ':collector'));

		if ($year) {
			$parameters['year'] = $year;
			$queryBuilder = $queryBuidler
					->andWhere($expr->eq('y.id', ':year'));
		}

		return $queryBuilder
						->orderBy('y.year', 'ASC')
						->addOrderBy('w.short_name', 'ASC')
						->addOrderBy('v.value', 'DESC')
						->setParameters($parameters)
						->getQuery()
						->getResult();
	}

	public function findYearsForCountry(Country $country, $collector) {
		$queryBuidler = $this->createQueryBuilder('c');
		$expr = $queryBuidler->expr();

		return $queryBuidler
						->select('c, y, w')
						->join('c.year', 'y')
						->leftJoin('y.workshop', 'w')
						->where($expr->eq('c.country', ':country'))
						->andWhere($expr->eq('c.collector', ':collector'))
						->orderBy('y.year', 'ASC')
						->addOrderBy('w.short_name', 'ASC')
						->setParameters(array(
							'collector' => $collector,
							'country' => $country,
						))
						->getQuery()
						->getResult();
	}

}
